/* ==UserStyle==
@name         Brave Search Tokyo Night (tokyonight)
@version      20240527.21.28
@namespace    https://userstyles.world/user/JoshuaMarkle
@description  Tokyonight for Brave!
@author       JoshuaMarkle
@license      CC Zero
==/UserStyle== */

@-moz-document domain("search.brave.com") {
* {
    --bg-dark: #16161E;
    --bg-code: #12121a;
    --bg: #1A1B26;
    --bg-2: #282B3C;
    --bg-3: #3F4256;
    --bg-4: #444B6A;
    --highlight-1: #1E202E;
    --highlight-2: #2E3448;
    --blue-accent: #89ddff;
    --text: #c0caf5;
    --text-dark: #565f89;
    --cyan: #7dcfff;
    --blue: #7aa2f7;
    --blue-dark: #5C88E6;
    --orange: #ff9e64;
    --orange-dark: #e3a144;
    --red: #f7768e;
    --red-dark: #db4b4b;
    --green: #9ece6a;
    --green-dark: #1abc9c;
    --purple: #bb9af7;
    --comment: #565f89;

    --color-primitive-black: #000000;
    --color-primitive-gray-01: #f3f5f7;
    --color-primitive-gray-10: #ebeef0;
    --color-primitive-gray-60: #546771;
    --color-primitive-primary-40: #7c91ff;
    --color-primitive-primary-60: #3f39e8;
    --color-primitive-primary-70: #3b08c2;
    --color-primitive-neutral-25: #3b3b3f;
    --color-primitive-white: #ffffff;
    --color-blue-20: #0b355d;
    --color-gray-10: #21272a;
    --color-gray-20: #30393d;
    --color-gray-30: #546771;
    --color-gray-40: #62757e;
    --color-gray-50: #a1b2ba;
    --color-gray-60: #becad0;
    --color-gray-70: #ebeef0;
    --color-green-10: #092c09;
    --color-green-20: #123f13;
    --color-green-30: #19721e;
    --color-green-40: #3c823c;
    --color-neutral-10: #111114;
    --color-neutral-60: #c7c7cc;
    --color-pink-40: #c24580;
    --color-primary-10: #1c0d64;
    --color-primary-20: #2a118e;
    --color-primary-30: #3f39e8;
    --color-primary-40: #545ff8;
    --color-primary-50: #7c91ff;
    --color-primary-60: #b7c6ff;
    --color-primary-70: #e9eeff;
    --color-purple-40: #7064d3;
    --color-red-20: #620f18;
    --color-red-40: #e11f3d;
    --color-secondary-40: #cd4400;
    --color-teal-40: #077f8d;
    --gradient-panel-background: linear-gradient( 96.98deg, #3b08c2 0%, #3f39e8 100% );
    --gradient-hero: linear-gradient(174deg, #ff4000 2.32%, #ff1f01 93.33%);
    --gradient-llm: linear-gradient( 180deg, rgba(36, 39, 49, .3) .09%, rgba(36, 39, 49, .3) 51.51%, rgba(23, 25, 30, .3) 95.35% ), var(--color-page-background);
    --color-button-background: #3f39e8;
    --color-button-disabled: #687b854d;
    --color-container-background: var(--bg);
    --color-container-disabled: #c3c9d32e;
    --color-container-highlight: var(--bg-dark);
    --color-container-interactive: #1c0d64;
    --color-dialogs-scrim-background: #00000066;
    --color-divider-interactive: #545ff866;
    --color-divider-strong: #a1b2ba66;
    --color-divider-subtle: var(--bg-2);
    --color-icon-default: var(--text);
    --color-icon-disabled: var(--text-dark);
    --color-icon-interactive: var(--blue);
    --color-link-default: var(--blue);
    --color-link-visited: var(--purple);
    --color-page-background: var(--bg-dark);
    --color-systemfeedback-error-background: #460a10;
    --color-systemfeedback-error-text: #ffb1b0;
    --color-systemfeedback-info-text: #a0cdff;
    --color-systemfeedback-success-text: #94df91;
    --color-tabs-search-text-default: #ff6022;
    --color-text-disabled: #ebeef080;
    --color-text-interactive: var(--text);
    --color-text-primary: var(--text);
    --color-text-secondary: var(--text);
    --color-text-tertiary: var(--text-dark);
    --color-serp-bar-bg: var(--bg-dark);
    --color-serp-breadcrumbs: var(--text-dark);
    --color-serp-container-interactive: #2b314b;
    --color-serp-divider-subtle-container: #30393d;
    --color-serp-header-background: var(--bg);
    --color-serp-highlight-background: var(--bg);
    --color-serp-snippet-background: var(--bg);
    --color-code-01: var(--text-dark);
    --color-code-02: var(--text);
    --color-code-03: var(--green);
    --color-code-04: var(--blue);
    --color-code-05: var(--purple);
    --color-code-border: var(--bg-2);
    --elevation-primary: rgba(255, 255, 255, .15);
    --elevation-secondary: rgba(0, 0, 0, .8);
}

#pagination-snippet { background-color: #ffffff00 }
.premium-cta { display: none; }
.SettingsDrawer { box-shadow: none !important; }
}

/* ==UserStyle==
@name         Github Tokyo Night (tokyonight)
@namespace    lol
@author       JoshuaMarkle
@description  Tokyonight for GitHub!
@version      1.4
@license      MIT
==/UserStyle== */
@-moz-document domain("github.com") {

    * {
        --bg: #1A1B26;
        --bg-2: #282B3C;
        --bg-3: #3F4256;
        --bg-4: #444B6A;
        --bg-dark: #16161E;
        --bg-code: #12121a;
        --highlight-1: #1E202E;
        --highlight-2: #2E3448;
        --blue-accent: #89ddff;
        --blue-dim: #5C88E6;
        --text: #c0caf5;
        --comment: #565f89;
        --border: var(--bg-2);
        --cyan: #7dcfff;
        --blue: #7aa2f7;
        --blue-dark: #5C88E6;
        --orange: #ff9e64;
        --red: #f7768e;
        --red-dark: #db4b4b;
        --green: #9ece6a;
        --green-dark: #1abc9c;
        --purple: #bb9af7; 

        --graph1: #9ece6aff;
        --graph2: #9ece6abb;
        --graph3: #9ece6a88;
        --graph4: #9ece6a33;

        --page-header-bgColor: var(--bg) !important;
        --header-bgColor: var(--bg) !important;
        --AppHeader-bg: var(--bg) !important;
        --underlineNav-borderColor-active: var(--orange) !important;

        --button-default-fgColor-rest: var(--text) !important;
        --button-default-bgColor-rest: var(--bg-dark) !important;
        --button-default-bgColor-hover: var(--highlight-1);
        --button-default-bgColor-active: var(--highlight-1);
        --button-default-borderColor-rest: var(--border) !important;
        --button-default-borderColor-hover: var(--text) !important;
        --button-default-borderColor-active: var(--comment) !important;
        --button-primary-bgColor-rest: var(--blue-dark);
        --button-primary-bgColor-hover: var(--blue-dark);
        --button-primary-bgColor-active: var(--blue-dark);
        --buttonCounter-default-bgColor-rest: var(--bg-2) !important;

        --borderColor-default: var(--border) !important;
        --borderColor-muted: var(--border);
        --borderColor-neutral-muted: var(--border) !important;
        --borderColor-attention-emphasis: var(--orange);
        --borderColor-danger-emphasis: var(--red-dark);

        --fgColor-default: var(--text) !important;
        --fgColor-disabled: var(--comment) !important;
        --fgColor-muted: var(--comment) !important;
        --fgColor-success: var(--green-dark);
        --fgColor-onEmphasis: var(--text) !important;
        --fgColor-accent: var(--blue);
        --fgColor-danger: var(--red);
        --fgColor-attention: var(--orange);

        --bgColor-emphasis: var(--highlight-1) !important;
        --bgColor-neutral-muted: var(--highlight-1);
        --bgColor-default: var(--bg) !important;
        --bgColor-muted: var(--bg) !important;
        --bgColor-inset: var(--bg-dark) !important;

        --control-bgColor-active: var(--highlight-1);
        --controlTrack-bgColor-rest: var(--bg);
        --controlTrack-bgColor-hover: var(--highlight-1);
        --controlTrack-bgColor-active: var(--highlight-2);
        --control-transparent-bgColor-selected: var(--highlight-1);
        --control-transparent-bgColor-active: var(--highlight-2);
        --control-transparent-bgColor-hover: var(--highlight-1);
        --control-borderColor-rest: var(--border) !important;
        --controlKnob-borderColor-rest: var(--border);
        --controlKnob-bgColor-rest: var(--bg);

        /* Code colors */
        --color-prettylights-syntax-comment: var(--comment);
        --color-prettylights-syntax-constant: var(--text);
        --color-prettylights-syntax-keyword: var(--purple);
        --color-prettylights-syntax-constant-other-reference-link: var(--cyan);
        --color-prettylights-syntax-entity: var(--blue);
        --color-prettylights-syntax-string: var(--green);
        --color-prettylights-syntax-storage-modifier-import: var(--green);
        --color-prettylights-syntax-entity-tag: var(--green-dark);
        --color-prettylights-syntax-variable: var(--orange);
        --color-prettylights-syntax-brackethighlighter-unmatched: var(--red); /* #f85149; */
        --color-prettylights-syntax-brackethighlighter-angle: var(--comment); /* #8b949e; */
        --color-prettylights-syntax-invalid-illegal-text: var(--text); /* #f0f6fc; */
        --color-prettylights-syntax-carriage-return-text: var(--text); /* #f0f6fc; */
        --color-prettylights-syntax-string-regexp: var(--green); /* #7ee787; */
        --color-prettylights-syntax-markup-list: var(--orange); /* #f2cc60; */
        --color-prettylights-syntax-markup-heading: var(--blue); /* #1f6feb; */
        --color-prettylights-syntax-markup-italic: var(--text); /* #c9d1d9; */
        --color-prettylights-syntax-markup-bold: var(--text) /* #c9d1d9; */
        --color-prettylights-syntax-markup-deleted-text: var(--red-dark); /* #ffdcd7; */
        --color-prettylights-syntax-markup-inserted-text: var(--green-dark); /* #aff5b4; */
        --color-prettylights-syntax-markup-changed-text: var(--orange); /* #ffdfb6; */
        --color-prettylights-syntax-markup-ignored-text: var(--comment); /* #c9d1d9; */
        --color-prettylights-syntax-meta-diff-range: var(--purple); /* #d2a8ff; */
        --color-prettylights-syntax-sublimelinter-gutter-mark: var(--comment) /* #484f58; */

        /* Builtin Editor */
        --codeMirror-lines-bgColor: var(--bg-dark);;
        --codeMirror-lines-bg: var(--bg-dark);;
        --codeMirror-bgColor: var(--bg-dark);;
        --codeMirror-gutters-bgColor: var(--bg-dark);;
        --codeMirror-gutterMarker-fgColor-default: var(--bg-dark);;
        --codeMirror-lines-bgColor: var(--bg-dark);;
        --codeMirror-gutterMarker-fgColor-muted: var(--comment);
        --codeMirror-lineNumber-fgColor: var(--comment);
        --codeMirror-fgColor: var(--text);

        /* Git Activity Table
        --color-calendar-halloween-graph-day-L2-bg: var(--red-dark) !important;
        --color-calendar-halloween-graph-day-L2-bg: var(--red) !important;
        --color-calendar-halloween-graph-day-L3-bg: var(--orange) !important;
        --color-calendar-halloween-graph-day-L4-bg: var(--green) !important;
        --color-calendar-graph-day-bg: var(--bg-dark) !important;
        --color-calendar-graph-day-L1-bg: var(--graph4) !important;
        --color-calendar-graph-day-L2-bg: var(--graph3) !important;
        --color-calendar-graph-day-L3-bg: var(--graph2) !important;
        --color-calendar-graph-day-L4-bg: var(--graph1) !important;
        --color-user-mention-fg: var(--orange) !important;
        --color-user-mention-bg: var(--orange) !important;
        */
    }

    body { background: var(--bg-dark); }

    /* Code Blocks */
    .js-snippet-clipboard-copy-unpositioned .markdown-body .snippet-clipboard-content, .js-snippet-clipboard-copy-unpositioned .markdown-body .highlight, .markdown-body .highlight pre, .markdown-body pre {
        /* background: var(--bg-dark); */
        border-radius: 8px;
    }



    .rounded-1 { border-radius: 8px !important; }
    .color-bg-subtle, .bgColor-muted { background: var(--bg-dark) !important; }

    /* Issues */
    nav.color-bg-default, .bgColor-default { background-color: var(--bg-dark) !important; }
}
@-moz-document regexp("https://github.com/.*/blob/.*") {

    * {
        --bg: #1A1B26;
        --bg-dark: #16161E;
        --bg-code: #12121a;
    }

    /* Special Code Blocks */
    .js-snippet-clipboard-copy-unpositioned .markdown-body .snippet-clipboard-content, .js-snippet-clipboard-copy-unpositioned .markdown-body .highlight, .markdown-body .highlight pre, .markdown-body pre {
        background: var(--bg-dark) !important;
    }
}

/* ==UserStyle==
@name         Reddit Tokyo Night (tokyonight)
@version      1.5
@namespace    https://userstyles.world/user/JoshuaMarkle
@description  Tokyo Night for Reddit!
@author       JoshuaMarkle
@license      CC Zero
==/UserStyle== */

@-moz-document domain("reddit.com") {
    * {
        --bg-dark: #16161E;
        --bg-code: #12121a;
        --bg: #1A1B26;
        --bg-2: #282B3C;
        --bg-3: #3F4256;
        --bg-4: #444B6A;
        --highlight: #1E202E;
        --highlight-2: #2E3448;
        --blue-accent: #89ddff;
        --blue-dim: #5C88E6;
        --text: #c0caf5;
        --text-dark: #565f89;
        --cyan: #7dcfff;
        --blue: #7aa2f7;
        --orange: #ff9e64;
        --red: #f7768e;
        --red-dark: #db4b4b;
        --green: #9ece6a;
        --green-dark: #1abc9c;
        --comment: #565f89;
        --border: var(--bg-2);

        --color-primary-onBackground: white !important;
        --color-primary-onBackground-selected: black;
        --color-neutral-border: var(--border) !important;

        --color-neutral-content: var(--text) !important;
        --color-neutral-content-disabled: var(--text-dark) !important;
        --color-neutral-content-weak: var(--text-dark) !important;
        --color-neutral-content-strong: var(--text) !important;
        --color-neutral-background: var(--bg-dark) !important;
        --color-neutral-background-selected: var(--blue-dim) !important;
        --color-neutral-background-weak: var(--bg) !important;
        --color-neutral-background-medium: var(--bg) !important;
        --color-neutral-background-strong: var(--bg) !important;
        --color-neutral-background-hover: var(--bg) !important;
        --color-neutral-border-weak: var(--border) !important;
        --color-neutral-border-strong: var(--border) !important;
        --color-tone-1: var(--text) !important;
        --color-tone-2: var(--text) !important;
        --color-tone-3: var(--bg-2) !important;
        --color-tone-4: var(--bg-2) !important;
        --color-tone-5: var(--bg-2) !important;
        --color-tone-6: var(--bg-2) !important;
        --color-tone-7: var(--bg-2) !important;
        --color-primary: var(--blue-dim) !important;
        --color-primary-hover: var(--blue-dim) !important;
        --color-primary-background: var(--blue) !important;
        --color-primary-background-hover: var(--blue-dim) !important;
        --color-primary-background-selected: var(--blue-dim) !important;
        --color-secondary: var(--text) !important;
        --color-secondary-hover: var(--text);
        --color-secondary-weak: var(--text-dark) !important;
        --color-secondary-background: var(--highlight) !important;
        --color-secondary-background-hover: var(--highlight) !important;
        --color-secondary-background-selected: var(--highlight-2) !important;
        --shreddit-content-background: var(--color-neutral-background) !important;

        --button-border-color: transparent;
        --button-border-color-activated: var(--border) !important;
        --button-color-background-activated: var(--highlight-2) !important;
        --button-color-text-activated: var(--text) !important;
        --button-color-overlay: var(--color-button-overlay-focus) !important;
        --button-border-color-hover: var(--border) !important;
        --button-color-background-hover: var(--highlight-2) !important;
        --button-color-text: var(--button-color-text-hover, var(--button-color-text-default)) !important;

        --color-button-secondary-text: var(--text) !important;
        --color-button-secondary-text-activated: var(--text) !important;
        --color-button-secondary-text-disabled: var(--text) !important;
        --color-button-secondary-border: var(--border) !important;
        --color-button-secondary-background: var(--highlight) !important;
        --color-button-secondary-background-focus: var(--highlight-2) !important;
        --color-button-secondary-background-hover: var(--highlight-2) !important;
        --color-button-secondary-background-activated: var(--highlight) !important;
        --color-button-secondary-background-disabled: var(--highlight) !important;

        --color-action-upvote: var(--blue);
        --color-action-downvote: var(--red);
        --color-media-background: var(--bg);
        --color-media-onbackground: var(--text);
        --color-media-onbackground-disabled: var(--highlight-2);

        --newCommunityTheme-bodyText: var(--text);
        --newCommunityTheme-metaText: var(--text-dark);
        --newCommunityTheme-button: var(--text);
        --newCommunityTheme-body: var(--bg-dark);
        --newRedditTheme-actionIcon: var(--text);
        --newCommunityTheme-line: var(--text-dark);
        --newRedditTheme-line: var(--text-dark);
        --newRedditTheme-linkText: var(--blue);
        --newCommunityTheme-navIconFaded10: var(--highlight-2);
        --newCommunityTheme-active: var(--blue-dim);
        --newCommunityTheme-field: var(--bg-dark);
        --newRedditTheme-button: var(--text);
        --newCommunityTheme-actionIcon: var(--text);
        --newCommunityTheme-bodyTextAlpha20: transparent;
        --newRedditTheme-post: var(--bg);
        --newRedditTheme-body: var(--bg-dark);
        --newRedditTheme-field: var(--bg-dark);
        --newCommunityTheme-navIcon: var(--text);
        --newRedditTheme-actionIconAlpha50: var(--text);
        --newRedditTheme-buttonAlpha05: var(--bg-dark);
        --newRedditTheme-bodyText: var(--text);
        --newCommunityTheme-buttonAlpha50: var(--text);
        --pseudo-before-background: var(--bg-dark) !important;
        --newRedditTheme-metaText: var(--text-dark);
        --newRedditTheme-navIconFaded10: var(--highlight);

        --button-color-background-default: transparent;
        --button-color-background-focus: transparent;
        --button-color-background-active: linear-gradient(transparent, transparent);
        --button-color-background-disabled: transparent;
        --button-color-text-default: var(--color-secondary-plain);
        --button-color-text-disabled: var(--color-interactive-content-disabled);
        --button-color-text-hover: var(--color-secondary-plain-hover);
        --button-border-color-default: var(--color-neutral-border-medium);
        --button-border-color-active: var(--color-neutral-border-strong);
        --button-border-color-disabled: var(--border) !important;
        --button-border-width-default: var(--border) !important;
        --button-border-width-activated: var(--border) !important;

        --newCommunityTheme-postLine: var(--text-dark);
        --newRedditTheme-widgetColors-sidebarWidgetBackgroundColor: var(--bg-dark);
        --newCommunityTheme-widgetColors-sidebarWidgetHeaderColor: var(--bg-dark);
        --newRedditTheme-widgetColors-sidebarWidgetBorderColor: var(--border);
        --post-icon-color: var(--text);
        --newCommunityTheme-postIcon: var(--text);
        --posttitletextcolor: var(--text);

        color: var(--text);
    }

    svg { fill: var(--text); }

    /* Background */
    .bg-neutral-background {
        background-color: var(--bg);
        border-bottom: none;
    }

    /* Borders */
    .xs\:rounded-\[16px\] {
        border-radius: 8px;
        border: 1px solid var(--border);
    }
    .py-md { 
        border: 1px solid var(--border);
        border-radius: 8px;
    }

    /* Images */
    .rounded-\[16px\] {
        border-radius: 8px;
        border: none;
    }

    /* Spacing between reddit posts */
    .border-b-neutral-border-weak {
        border: none;
        height: 0.5rem;
    }

    .flair-content { color: white !important; }

    /* Store */
    .text-global-brand-orangered { background: color-mix(in srgb, var(--orange) 20%, transparent); }
    .text-global-brand-orangered faceplate-number { color: var(--orange); }
}

/* ==UserStyle==
@name         ChatGPT Tokyonight
@version      2.2
@namespace    https://userstyles.world/user/JoshuaMarkle
@description  A tokyonight (dark) themed ChatGPT! The colors are easily customizable if you want to change the theme yourself :)
@author       JoshuaMarkle
@license      CC Zero
==/UserStyle== */

@-moz-document domain("chatgpt.com") {
    * {
        --bg-dark: #16161E;
        --bg-code: #12121a;
        --bg: #1A1B26;
        --bg-2: #282B3C;
        --bg-3: #3F4256;
        --bg-4: #444B6A;
        --highlight-1: #1E202E;
        --highlight-2: #2E3448;
        --blue-accent: #89ddff;
        --blue-2: #5C88E6;
        --text: #c0caf5;
        --text-2: #565f89;
        --cyan: #7dcfff;
        --blue: #7aa2f7;
        --orange: #ff9e64;
        --red: #f7768e;
        --red-dark: #db4b4b;
        --green: #9ece6a;
        --green-dark: #1abc9c;
        --comment: #565f89;

        --main-surface-primary: var(--bg);
        --main-surface-secondary: var(--bg-2);
        --main-surface-tertiary: var(--bg-3);
        --sidebar-surface-primary: var(--bg-dark);
        --sidebar-surface-secondary: var(--bg);
        --sidebar-surface-tertiary: var(--bg-2);
        --text-primary: var(--text);
        --text-secondary: var(--text-2);
        --text-tertiary: var(--text-2);
        --text-quaternary: var(--text-2);
        --border: var(--bg-2);
        --border-light: var(--border);
        --border-medium: var(--border);
        --border-heavy: var(--border);
        --border-xheavy: var(--border);
        --link: var(--blue);
        --link-hover: var(--blue-2);
        --tw-prose-bold: var(--text);
        --tw-prose-headings: var(--text);
        --theme-user-msg-bg: var(--bg-2);
        --default-theme-user-msg-bg: var(--bg-2);
        --menu-item-highlighted: var(--bg-2);
        --menu-item-active: var(--bg);
        --menu-item-open: var(--bg-2);

        --bg-primary: var(--bg);
        --bg-secondary: var(--bg-2);
        --bg-tertiary: var(--bg-3);
        --bg-scrim: #0d0d0d80;
        --bg-elevated-primary: var(--bg-2);
