    /* Main prompt box */
    *::placeholder {
        color: var(--text-2);
    }
    #composer-submit-button { background: var(--green-dark) !important; }
    #composer-submit-button > svg { color: white !important; }
    .text-white, textarea {
        color: var(--text) !important;
    }
    .bg-white {
        color: var(--bg-dark);
        background-color: var(--green-dark) !important;
        border: none;
    }
    .bg-white .text-white {
        color: var(--bg-dark) !important;
    }
    .dark .text-gray-500 { color: var(--text-2); }
    svg { color: var(--text); }

    .dark\:bg-\[\#303030\]:is(.dark *) {
        background: var(--bg-2);
    }

    /* Full width */
    div, .text-gray-100 {
        color: var(--text);
    }

    /* Small border on navigation bar */
    nav {
        border-right: 1px solid var(--border) !important;
    }

    /* Code block */
    pre > div { border: 1px solid var(--border) !important; }
    code {
        color: var(--text) !important;
    }
    .p-4 {
        background: var(--bg-code);
    }
    .hljs-meta {
        color: var(--cyan);
    }
    .hljs-title {
        color: var(--blue);
    }
    .hljs-type {
        color: var(--blue-2);
    }
    .hljs-number {
        color: var(--orange);
    }
    .hljs-built_in {
        color: var(--blue);
    }
    .hljs-string {
        color: var(--green);
    }
    .hljs-keyword {
        color: var(--cyan);
    }
    .hljs-comment {
        color: var(--comment);
    }

    /* Settings */
    .btn-danger {
        background-color: var(--red-dark);
    }
    .btn-danger div {
        color: var(--bg-dark) !important;
    }

    .btn-primary {
        background-color: var(--green-dark);
    }
    .btn-primary div {
        color: var(--bg-dark) !important;
    }

    .bg-gray-900 {
        background-color: var(--bg-dark);
        border: 1px solid var(--border);
        border-radius: 1px;
    }

    /* Pop Up */
    .popover { background: var(--bg-2) !important; }
    .hover\:bg-token-main-surface-secondary:hover { background: var(--bg); }
    .bg-white { background: var(--text); }
    .rounded-2xl { border-radius: 10px; }
    .dark .dark\:bg-black\/80 { background: rgba(0,0,0,0.4)} 

